mapper.select:
select dfu.nickname driver_nickname, dfu.avatar driver_avatar, dfu.mobile driver_mobile, ufu.nickname user_nickname, ufu.avatar user_avatar, ufu.mobile user_mobile, fdg.load_province_code, fdg.load_city_code, fdg.load_district_code, fdg.unload_province_code, fdg.unload_city_code, fdg.unload_district_code, fdg.load_start_at, fdg.load_end_at, fo.amount, fo.pay_status, fo.refund_status, fo.confirm_status, fo.evaluate_status, fo.cancel_status, fo.driver_evaluate_status, fo.transport_status, fo.driver_pay_status, fo.user_id, fo.driver_id, fo.id order_id, fo.delivery_id, fo.protocol_status, from fo_order fo left join sy_user ufu on ufu.id = fo.user_id left join sy_user dfu on dfu.id = fo.driver_id left join fo_deliver_goods fdg on fo.delivery_id = fdg.id
1=1
and fo.user_id = #{userId}
and fo.cancel_status = #{cancelStatus}
and fo.confirm_status = #{confirmStatus}
and fo.delivery_id = #{deliveryId}
and fo.driver_evaluate_status = #{driverEvaluateStatus}
and fo.evaluate_status = #{evaluateStatus}
and fo.driver_id = #{driverId}
and fo.pay_status = #{payStatus}
and fo.refund_status = #{refundStatus}
and fo.transport_status = #{transportStatus}
and fo.driver_pay_status = #{driverPayStatus}
and fo.protocol_status = 1
and fo.protocol_status != 1
